import{_ as i}from"./plugin-vue_export-helper-c27b6911.js";import{r as n,o,c,a as e,b as t,f as r,d as s}from"./app-5cc82e9c.js";const l={},p=s('<h1 id="设置网页版工作站环境" tabindex="-1"><a class="header-anchor" href="#设置网页版工作站环境" aria-hidden="true">#</a> 设置网页版工作站环境</h1><p>我们在电脑上，工作站可以通过 Chrome浏览器（谷歌浏览器）或者 Edge 浏览器访问。因为微软提供的工作系统在浏览器上可以实时简易访问。</p><h2 id="什么是区分环境" tabindex="-1"><a class="header-anchor" href="#什么是区分环境" aria-hidden="true">#</a> 什么是区分环境</h2><p>前面说过，个人账户不可以和工作账户或教育账户进行混淆，应当予以区分。试想一下，</p><ul><li>你的 Microsoft 个人账户是：name@outlook.com</li><li>你的工作站账户是：name@xxxx.edu</li></ul><p>您处理个人的隐私任务需要使用个人账户，而处理和组织相关的工作或学习事务需要使用工作站账户，未来就业后还可能有公司分发的企业账户。请问你应当怎样同时登入这些账户，而且保证信息分离，切换自如呢？</p><p>无论是 Chrome浏览器（谷歌浏览器）还是 Edge 浏览器，都为我们提供了“多个人资料”功能。资料卡之间相互独立，相当于独立的容器。</p><ul><li>容器可以随意创建，改名和删除。</li><li>容器之间相互独立的，你在一个容器中做的操作，比如保存密码，登入账户都不会影响到另一个容器。</li></ul><p>这是我们要建立多个资料卡的原因！</p><ul><li>个人相关事宜，应当在个人账户环境下完成。</li><li>组织相关事宜，应当在工作账户环境下完成。</li></ul><p><strong>两者相互切换，可以保证你的个人账户和工作账户不会混淆。很多人没有进行区分，导致后期浏览器或者账户出现了很多错误！</strong></p><div class="hint-container warning"><p class="hint-container-title">注意</p><p>“多个人资料”功能仅限于电脑上的浏览器，手机想实现类似功能请考虑使用“应用分身”功能。</p></div><h2 id="microsoft-edge-个人资料设置" tabindex="-1"><a class="header-anchor" href="#microsoft-edge-个人资料设置" aria-hidden="true">#</a> Microsoft Edge 个人资料设置</h2><p>首先我们需要配置好个人账户，如果 Windows 系统已经登入了Microsoft 个人账户，一般将自动登入。</p><figure><img src="https://static-file.asi.ac.cn/2022/07/16/4882d44fae4b9.png" alt="1657904449567.png" tabindex="0" loading="lazy"><figcaption>1657904449567.png</figcaption></figure><p>下一步添加教育账户资料</p><figure><img src="https://static-file.asi.ac.cn/2022/07/16/f9b26c56ff451.png" alt="1657904788935.png" tabindex="0" loading="lazy"><figcaption>1657904788935.png</figcaption></figure><figure><img src="https://static-file.asi.ac.cn/2022/07/16/ce5b184c027c8.png" alt="1657905509158.png" tabindex="0" loading="lazy"><figcaption>1657905509158.png</figcaption></figure><h2 id="chrome-个人资料设置" tabindex="-1"><a class="header-anchor" href="#chrome-个人资料设置" aria-hidden="true">#</a> Chrome 个人资料设置</h2>',19),d={href:"https://www.google.cn/chrome",target:"_blank",rel:"noopener noreferrer"},h=e("img",{src:"https://static-file.asi.ac.cn/2022/07/16/d8ed0a68a794f.png",alt:"1657905575293.png",style:{zoom:"33%"}},null,-1),g=e("p",null,"安装完成后，我们应当添加一份工作账户资料。",-1),f=e("img",{src:"https://static-file.asi.ac.cn/2022/07/16/cdee6951d8cea.png",alt:"1657905607773.png",style:{zoom:"33%"}},null,-1),m=e("p",null,"此时你应该至少有两份资料，一份是个人账户，一份是工作账户。",-1);function u(_,x){const a=n("ExternalLinkIcon");return o(),c("div",null,[p,e("p",null,[t("首先，我们需要下载Chrome，下载地址 "),e("a",d,[t("https://www.google.cn/chrome"),r(a)]),t(" 。需要注意的是请不要使用其他链接下载， 考虑到网络上流传了很多盗版的 Chrome浏览器。")]),h,g,f,m])}const v=i(l,[["render",u],["__file","set-environment.html.vue"]]);export{v as default};
